mapper.select:
SELECT se.*,
e1.name as salesperson_name,
c.name as customer_name,
e2.name as current_handler_name
FROM sales_emergency se
LEFT JOIN employees e1 ON se.salesperson_id = e1.id
LEFT JOIN customers c ON se.customer_id = c.id
LEFT JOIN employees e2 ON se.current_handler_id = e2.id
WHERE se.is_deleted = 0
AND se.salesperson_id = #{salespersonId}
AND se.current_handler_id = #{handlerId}
AND se.status = #{status}
AND se.type = #{type}
AND se.urgency = #{urgency}
AND se.customer_id = #{customerId}
AND se.created_time >= #{startTime}
AND se.created_time <= #{endTime}
ORDER BY se.created_time DESC

mapper.update:
UPDATE sales_emergency
SET is_deleted = 1, updated_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT COUNT(*)
FROM sales_emergency
WHERE is_deleted = 0
AND salesperson_id = #{salespersonId}
AND status = #{status}
AND current_handler_id = #{handlerId}
